// page init
jQuery(function(){
	initMasonry();
	initTouchNav();
	initOpenClose();
	initSameHeight();
	jQuery('input, textarea').placeholder();
});

// open-close init
function initOpenClose() {
	jQuery('.tab-box').openClose({
		hideOnClickOutside: true,
		activeClass: 'active',
		opener: '.opener',
		slider: '.slide',
		animSpeed: 400,
		effect: 'slide'
	});
}

// masonry init
function initMasonry() {
	jQuery(window).on('load masonry/refresh', function() {
		jQuery('.js-masonry').masonry('layout');
	});
}

// align blocks height
function initSameHeight() {
	jQuery('.assurance-list').sameHeight({
		elements: '.box',
		flexible: true,
		multiLine: true
	});
}

// handle dropdowns on mobile devices
function initTouchNav() {
	jQuery('.nav').each(function(){
		new TouchNav({
			navBlock: this
		});
	});
}

